import java.util.*;
import java.util.regex.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        sc.nextLine();
        String[] s = new String[n];
        for (int i = 0; i < n; i++) {
            s[i] = sc.nextLine();
        }
        String t = sc.nextLine();
        Set<Integer> ans = new TreeSet<>();
        List<String> list = new ArrayList<>();
        for (int i = 0; i < (1 << countQ(s)); i++) {
            list.clear();
            list.add(t);
            int cur = i;
            for (String str : s) {
                String filled = "";
                for (char c : str.toCharArray()) {
                    if (c == '?') {
                        filled += (cur % 2 == 0) ? 'a' : 'z';
                        cur /= 2;
                    } else {
                        filled += c;
                    }
                }
                list.add(filled);
            }
            Collections.sort(list);
            ans.add(list.indexOf(t) + 1);
        }
        for (int a : ans) {
            System.out.print(a + " ");
        }
        System.out.println();
    }

    public static int countQ(String[] s) {
        int count = 0;
        for (String str : s) {
            for (char c : str.toCharArray()) {
                if (c == '?') {
                    count++;
                }
            }
        }
        return count;
    }
}
