#include <stdio.h>
#include <limits.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    long long a[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &a[i]);
    }
    long long dp[1 << N][N];
    for (int i = 0; i < (1 << N); i++) {
        for (int j = 0; j < N; j++) {
            dp[i][j] = LLONG_MAX;
        }
    }
    for (int i = 0; i < N; i++) {
        dp[1 << i][i] = 0;
    }
    for (int mask = 1; mask < (1 << N); mask++) {
        for (int i = 0; i < N; i++) {
            if ((mask >> i) & 1) {
                for (int j = 0; j < i; j++) {
                    if ((mask >> j) & 1) {
                        long long cost = 0;
                        long long max_height = a[j];
                        for (int k = j + 1; k <= i; k++) {
                            if ((mask >> k) & 1) {
                                max_height = (max_height > a[k]) ? max_height : a[k];
                            }
                        }
                        for (int k = j + 1; k <= i; k++) {
                            if ((mask >> k) & 1) {
                                cost += max_height - a[k] + 1;
                            }
                        }
                        if (dp[mask][i] > dp[mask ^ (1 << i)][j] + cost) {
                            dp[mask][i] = dp[mask ^ (1 << i)][j] + cost;
                        }
                    }
                }
            }
        }
    }
    long long min_cost = LLONG_MAX;
    for (int i = 0; i < N; i++) {
        long long cost = 0;
        long long max_height = a[i];
        for (int j = i + 1; j < K; j++)
          {
            if((1<<(j)) & ((1<<N)-1))
            {
              max_height = (max_height > a[j]) ? max_height : a[j];
            }
          }
          for (int j = i + 1; j < K; j++) {
            if((1<<(j)) & ((1<<N)-1))
            {
              cost += max_height - a[j] + 1;
            }
          }
        if(dp[(1 << K) -1][i] != LLONG_MAX)
        {
          if (min_cost > dp[(1 << K) - 1][i] + cost) {
            min_cost = dp[(1 << K) - 1][i] + cost;
          }
        }
    }
    printf("%lld\n", min_cost);
    return 0;
}
