#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long min_cost = -1;

  for (long long i = 0; i < (1LL << n); ++i) {
    long long current_cost = 0;
    vector<long long> current_heights = a;
    vector<int> visible_buildings;
    long long visible_count = 0;

    for (int j = 0; j < n; ++j) {
      bool visible = true;
      if ((i >> j) & 1) {
        long long max_height = 0;
        for (int l = 0; l < j; ++l) {
          max_height = max(max_height, current_heights[l]);
        }
        current_cost += max(0LL, max_height + 1 - current_heights[j]);
        current_heights[j] = max(current_heights[j], max_height + 1);
      }

      for(int l=0; l<j; ++l){
          if(current_heights[l] >= current_heights[j]){
              visible = false;
              break;
          }
      }
      if(visible) visible_count++;

    }

    if (visible_count >= k) {
      if (min_cost == -1 || current_cost < min_cost) {
        min_cost = current_cost;
      }
    }
  }
  cout << min_cost << endl;
  return 0;
}
