import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long ans = Long.MAX_VALUE;
        for (int i = 0; i < (1 << n); i++) {
            if (Integer.bitCount(i) < k) continue;
            long cur = 0;
            long max = 0;
            ArrayList<Integer> idx = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) idx.add(j);
            }
            for (int j = 0; j < idx.size(); j++) {
                int curIdx = idx.get(j);
                long need = 0;
                if (j > 0) {
                    need = max + 1 - a[curIdx];
                    if (need < 0) need = 0;
                } else {
                    need = max + 1 - a[curIdx];
                    if(need < 0) need = 0;
                }
                cur += need;
                max = a[curIdx] + need;
            }
            ans = Math.min(ans, cur);
        }
        System.out.println(ans);
    }
}
