#include <stdio.h>

#define MAXN 100000

int n, m, q;
int a[MAXN];
long long grid[MAXN][7];
int visited[MAXN][7];

int dx[] = {0, 0, 1, -1};
int dy[] = {1, -1, 0, 0};

void dfs(long long x, long long y) {
    visited[x][y] = 1;
    for (int i = 0; i < 4; i++) {
        long long nx = x + dx[i];
        long long ny = y + dy[i];
        if (nx >= 0 && nx < n && ny >= 0 && ny < 7 && !visited[nx][ny] && grid[nx][ny] % m != -1) {
            dfs(nx, ny);
        }
    }
}

int main() {
    scanf("%lld %d %d", &n, &m, &q);
    for (int i = 0; i < q; i++) {
        scanf("%d", &a[i]);
    }

    for (long long i = 0; i < n; i++) {
        for (int j = 0; j < 7; j++) {
            grid[i][j] = 7 * i + j - 8;
        }
    }

    for (int i = 0; i < q; i++) {
        for (long long j = 0; j < n; j++) {
            for (int k = 0; k < 7; k++) {
                if (grid[j][k] % m == a[i]) {
                    grid[j][k] = -1;
                }
            }
        }
    }

    int count = 0;
    for (long long i = 0; i < n; i++) {
        for (int j = 0; j < 7; j++) {
            if (grid[i][j] % m != -1 && !visited[i][j]) {
                dfs(i, j);
                count++;
            }
        }
    }
    printf("%d\n", count);
    return 0;
}
