#include <iostream>
#include <vector>

using namespace std;

int n, m, q;
vector<long long> a;

int dx[] = {0, 0, 1, -1};
int dy[] = {1, -1, 0, 0};

bool isValid(long long x, long long y) {
  return x >= 0 && x < n && y >= 0 && y < 7;
}

void dfs(long long x, long long y, vector<vector<bool>>& visited, vector<vector<long long>>& grid) {
  visited[x][y] = true;
  for (int i = 0; i < 4; ++i) {
    long long nx = x + dx[i];
    long long ny = y + dy[i];
    if (isValid(nx, ny) && !visited[nx][ny] && (grid[nx][ny] % m) != -1) {
      dfs(nx, ny, visited, grid);
    }
  }
}

int main() {
  cin >> n >> m >> q;
  a.resize(q);
  for (int i = 0; i < q; ++i) {
    cin >> a[i];
  }

  vector<vector<long long>> grid(n, vector<long long>(7));
  for (long long i = 0; i < n; ++i) {
    for (long long j = 0; j < 7; ++j) {
      grid[i][j] = 7 * i + j - 7;
    }
  }

  for (int i = 0; i < q; ++i) {
    for (long long j = 0; j < n; ++j) {
      for (long long k = 0; k < 7; ++k) {
        if (grid[j][k] % m == a[i]) {
          grid[j][k] = -1;
        }
      }
    }
  }

  vector<vector<bool>> visited(n, vector<bool>(7, false));
  int count = 0;
  for (long long i = 0; i < n; ++i) {
    for (long long j = 0; j < 7; ++j) {
      if (!visited[i][j] && (grid[i][j] % m) != -1) {
        dfs(i, j, visited, grid);
        count++;
      }
    }
  }

  cout << count << endl;
  return 0;
}
