import java.util.*;
import java.io.*;

public class CalendarPainting {

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        long n = Long.parseLong(st.nextToken());
        int m = Integer.parseInt(st.nextToken());
        int q = Integer.parseInt(st.nextToken());
        int[] a = new int[q];
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < q; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }

        boolean[][] grid = new boolean[(int) n][7];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 7; j++) {
                grid[i][j] = true;
            }
        }

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 7; j++) {
                int val = (int) (7 * i + j - 7);
                for (int k = 0; k < q; k++) {
                    if (val % m == a[k]) {
                        grid[i][j] = false;
                        break;
                    }
                }
            }
        }

        int count = 0;
        boolean[][] visited = new boolean[(int) n][7];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 7; j++) {
                if (grid[i][j] && !visited[i][j]) {
                    count++;
                    dfs(grid, visited, i, j);
                }
            }
        }
        System.out.println(count);
    }

    public static void dfs(boolean[][] grid, boolean[][] visited, int i, int j) {
        if (i < 0 || i >= grid.length || j < 0 || j >= 7 || !grid[i][j] || visited[i][j]) {
            return;
        }
        visited[i][j] = true;
        dfs(grid, visited, i + 1, j);
        dfs(grid, visited, i - 1, j);
        dfs(grid, visited, i, j + 1);
        dfs(grid, visited, i, j - 1);
    }
}
