#include <iostream>
#include <vector>
#include <iomanip>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<tuple<int, int>> edges;
  vector<vector<int>> adj(n);
  for (int i = 0; i < n - 1; ++i) {
    int u, v;
    cin >> u >> v;
    --u; --v;
    edges.emplace_back(u, v);
    adj[u].push_back(v);
    adj[v].push_back(u);
  }

  cout << fixed << setprecision(10);

  for (int start_node = 0; start_node < n; ++start_node) {
    double expected_turns = 0;
    vector<bool> visited(n, false);
    vector<int> path;
    function<void(int, double)> dfs = [&](int u, double prob) {
      visited[u] = true;
      path.push_back(u);

      bool can_move = false;
      for (int v : adj[u]) {
        if (!visited[v]) {
          can_move = true;
          dfs(v, prob / adj[u].size());
        }
      }
      if (!can_move) {
          expected_turns += prob * path.size();
      }
      visited[u] = false;
      path.pop_back();
    };
    dfs(start_node,1.0);
    cout << expected_turns << endl;
  }
  return 0;
}
