#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, k, x;
  cin >> n >> k >> x;

  vector<long long> s(k);
  for (int i = 0; i < k; ++i) {
    cin >> s[i];
  }

  long long count = 0;
  long long mod = 1e9 + 7;

  vector<long long> t;
  function<void(long long)> generate_subsets = [&](long long index) {
    if (index == n + 1) {
      bool ok = true;
      t.push_back(0);
      for (long long i = 0; i < k -1; ++i) {
          bool found = false;
          for (long long j = 0; j < t.size(); ++j){
              if (t[j] == s[i+1]){
                  found = true;
                  break;
              }
          }
          if (!found) ok = false;
      }
      if (ok) {
          long long time = 0;
          for (size_t i = 0; i < t.size() - 1; ++i) {
              time += abs(t[i+1] - t[i]);
          }
          if (time <=x) {
              bool satisfies_condition = true;
                for (long long i = 0; i < k; ++i) {
                    bool found = false;
                    for (long long j = 0; j < t.size(); ++j) {
                        if (t[j] == s[i]) {
                            found = true;
                            break;
                        }
                    }
                    if (!found) satisfies_condition = false;
                }
                if (satisfies_condition) count = (count + 1) % mod;
          }
      }
      t.pop_back();
      return;
    }

    generate_subsets(index + 1);
    t.push_back(index);
    generate_subsets(index + 1);
    t.pop_back();
  };

  generate_subsets(0);

  cout << count << endl;
  return 0;
}
