import java.util.*;
import java.lang.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        int x = sc.nextInt();
        int[] s = new int[k];
        for (int i = 0; i < k; i++) {
            s[i] = sc.nextInt();
        }
        long mod = 1000000007;
        long[][] dp = new long[k][n + 1];
        for (int i = 0; i <= n; i++) {
            dp[0][i] = 1;
        }
        for (int i = 1; i < k; i++) {
            for (int j = s[i]; j <= n; j++) {
                for (int l = s[i - 1]; l < j; l++) {
                    if (j - l <= x) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][l]) % mod;
                    }
                }
            }
        }
        System.out.println(dp[k - 1][n]);
    }
}
