def solve():
    n, k, x = map(int, input().split())
    s = list(map(int, input().split()))

    mod = 10**9 + 7

    ans = 0

    for i in range(1 << n):
        t = []
        for j in range(n + 1):
            if (i >> j) & 1:
                t.append(j)

        if len(t) == 0:
            continue

        if t[0] != 0 or t[-1] != n:
            continue

        valid = True
        for station in s:
            if station not in t:
                valid = False
                break

        if not valid:
            continue

        time_taken = 0


        curr = 0
        for next_stop in t[1:]:
          time_taken += next_stop - curr
          curr = next_stop

        if time_taken <=x:
          ans = (ans + 1) % mod

    print(ans)

