#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, Q;
    scanf("%d %d", &N, &Q);

    int *p = (int *)malloc(N * sizeof(int));
    long long *a = (long long *)malloc(N * sizeof(long long));
    for (int i = 0; i < N; i++) {
        scanf("%d %lld", &p[i], &a[i]);
    }

    int current_workers = N;

    for (int q = 0; q < Q; q++) {
        int type;
        scanf("%d", &type);

        if (type == 1) {
            int v, d;
            long long x;
            scanf("%d %d %lld", &v, &d, &x);
            a[v] += x;
            int current = v;
            for (int j = 1; j <= d; j++) {
                int count = 0;
                for (int i = 0; i < current_workers; i++) {
                    if (p[i] == current) {
                        a[i] += x;
                        count++;

                    }
                }
                if (count == 0) break;
                int next_current = -1;
                for (int i = 0; i < current_workers; i++) {
                    if (p[i] == current) {
                        next_current = i;
                        break;
                    }
                }
                if (next_current == -1) break;
                current = next_current;

            }
        } else if (type == 2) {
            int v, d;
            scanf("%d %d", &v, &d);
            long long sum = a[v];
            int current = v;
            for (int j = 1; j <= d; j++) {
                int count = 0;
                for (int i = 0; i < current_workers; i++) {
                    if (p[i] == current) {
                        sum += a[i];
                        count++;
                    }
                }
                if (count == 0) break;
                int next_current = -1;
                for (int i = 0; i < current_workers; i++) {
                    if (p[i] == current) {
                        next_current = i;
                        break;
                    }
                }
                if (next_current == -1) break;
                current = next_current;
            }
            printf("%lld\n", sum);
        } else {
            int pr;
            long long ar;
            scanf("%d %lld", &pr, &ar);
            p = (int *)realloc(p, (current_workers + 1) * sizeof(int));
            a = (long long *)realloc(a, (current_workers + 1) * sizeof(long long));
            p[current_workers] = pr;
            a[current_workers] = ar;
            current_workers++;
        }
    }

    free(p);
    free(a);
    return 0;
}
