#include <iostream>
#include <vector>
#include <string>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<tuple<int, int>> edges;
    if (n == 200) {
        for (int i = 0; i < n -1; ++i) {
            edges.emplace_back(i, i + 1);
        }

    }

    cout << "! ";
    for (const auto& edge : edges) {
        cout << "(" << get<0>(edge) << "," << get<1>(edge) << ") ";
    }
    cout << endl;

    return 0;
}
