import java.io.*;
import java.util.*;

public class Main {
    static BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    static PrintWriter out = new PrintWriter(System.out);
    static int n;
    static ArrayList<Pair> edges = new ArrayList<>();

    public static void main(String[] args) throws IOException {
        n = Integer.parseInt(br.readLine());
        for (int i = 0; i < n - 1; i++) {
            findEdge();
        }
        out.print("!");
        for (Pair edge : edges) {
            out.print(" (" + edge.first + "," + edge.second + ")");
        }
        out.println();
        out.close();
    }

    static void findEdge() throws IOException {
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                String query = "";
                for (int k = 0; k < n; k++) {
                    if (k == i || k == j) {
                        query += "1";
                    } else {
                        query += "0";
                    }
                }
                out.println("? " + query);
                out.flush();
                int response = Integer.parseInt(br.readLine());
                if (response > 0) {
                    edges.add(new Pair(i, j));
                    return;
                }
            }
        }
    }

    static class Pair {
        int first;
        int second;

        Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }
    }
}
