    for y in range(B):
        for z in range(C):
            found = False
            for x_prime in range(A):
                for y_prime in range(B):
                    for z_prime in range(C):
                        if x_prime * B * C + y_prime * C + z_prime == x * B * C + y * C + z:
                            if max(abs(x - x_prime), abs(y - y_prime), abs(z - z_prime)) <= D:
                                found = True
                                break
                    if found:
                        break
                if found:
                    break
            if found:
                count += 1

print(count % MOD)

