#include <stdio.h>
#include <string.h>

int main() {
    char O[51], E[51];
    scanf("%s", O);
    scanf("%s", E);
    int lenO = strlen(O);
    int lenE = strlen(E);
    char result[101];
    int i = 0, j = 0, k = 0;
    while (i < lenO || j < lenE) {
        if (i < lenO) {
            result[k++] = O[i++];
        }
        if (j < lenE) {
            result[k++] = E[j++];
        }
    }
    result[k] = '\0';
    printf("%s\n", result);
    return 0;
}
