#include <iostream>
#include <string>

using namespace std;

int main() {
  string o, e;
  cin >> o >> e;

  string result = "";
  int o_ptr = 0;
  int e_ptr = 0;

  while (o_ptr < o.length() || e_ptr < e.length()) {
    if (o_ptr < o.length()) {
      result += o[o_ptr];
      o_ptr++;
    }
    if (e_ptr < e.length()) {
      result += e[e_ptr];
      e_ptr++;
    }
  }
  cout << result << endl;
  return 0;
}
