import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String o = scanner.next();
        String e = scanner.next();
        StringBuilder password = new StringBuilder();
        int oIndex = 0;
        int eIndex = 0;
        boolean oTurn = true;
        while (oIndex < o.length() || eIndex < e.length()) {
            if (oTurn) {
                if (oIndex < o.length()) {
                    password.append(o.charAt(oIndex));
                    oIndex++;
                }
            } else {
                if (eIndex < e.length()) {
                    password.append(e.charAt(eIndex));
                    eIndex++;
                }
            }
            oTurn = !oTurn;
        }
        System.out.println(password);
        scanner.close();
    }
}
