#include <iostream>
#include <string>
#include <vector>
#include <map>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<map<char, int>> counts(n);
  vector<string> s(n);
  for (int i = 0; i < n; ++i) {
    cin >> s[i];
    for (char c : s[i]) {
      counts[i][c]++;
    }
  }

  map<char, int> min_counts;
  for (char c = 'a'; c <= 'z'; ++c) {
    int min_count = 1e9;
    for (int i = 0; i < n; ++i) {
      min_count = min(min_count, counts[i][c]);
    }
    min_counts[c] = min_count;
  }

  string best_s = "";
  for (char c = 'a'; c <= 'z'; ++c) {
    for (int i = 0; i < min_counts[c]; ++i) {
      best_s += c;
    }
  }

  cout << best_s << endl;
  return 0;
}
