#include <stdio.h>

#include <limits.h>

int main() {
    int N;
    scanf("%d", &N);
    long long a[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &a[i]);
    }
    long long max_yen = LLONG_MIN;
    for (int i = 0; i < (1 << N); i++) {
        long long current_yen = 0;
        long long smashed[N + 1];
        for (int j = 1; j <= N; j++) {
            smashed[j] = 0;
        }
        for (int j = 0; j < N; j++) {
            if ((i >> j) & 1) {
                for (int k = j + 1; k <= N; k++) {
                    if (k % (j + 1) == 0) {
                        smashed[k] = 1;
                    }
                }
            }
        }
        for (int j = 1; j <= N; j++) {
            if (smashed[j] == 0) {
                current_yen += a[j - 1];
            }
        }
        if (current_yen > max_yen) {
            max_yen = current_yen;
        }
    }
    printf("%lld\n", max_yen);
    return 0;
}
