#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);

    int a[n], b[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
        a[i]--; 
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &b[i]);
        b[i]--; 
    }

    long long count = 0;
    int c[n];
    int p[n];
    for (int i = 0; i < n; i++) p[i] = i;

    do {
        for (int i = 0; i < n; i++) c[i] = p[i];
        int remaining[n];
        for (int i = 0; i < n; i++) remaining[i] = 1;
        int eaten = 0;
        int fight = 0;

        while (eaten < n && fight == 0) {
            int a_choice = -1, b_choice = -1, c_choice = -1;
            int a_min = -1, b_min = -1, c_min = -1;


            for (int i = 0; i < n; i++) {
                if (remaining[a[i]]){
                    a_choice = a[i];
                    break;
                }
            }
            for (int i = 0; i < n; i++) {
                if (remaining[b[i]]){
                    b_choice = b[i];
                    break;
                }
            }
            for (int i = 0; i < n; i++) {
                if (remaining[c[i]]){
                    c_choice = c[i];
                    break;
                }
            }


            if (a_choice != b_choice && a_choice != c_choice && b_choice != c_choice) {
                remaining[a_choice] = 0;
                remaining[b_choice] = 0;
                remaining[c_choice] = 0;
                eaten += 3;
            } else {
                fight = 1;
            }
        }
        if (fight == 0) count++;
    } while (next_permutation(p, p + n));

    printf("%lld\n", count % MOD);
    return 0;
}
