#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    char s[50][51];
    for (int i = 0; i < n; i++) {
        scanf("%s", s[i]);
    }
    int freq[26] = {0};
    for (int i = 0; i < n; i++) {
        int temp[26] = {0};
        for (int j = 0; j < strlen(s[i]); j++) {
            temp[s[i][j] - 'a']++;
        }
        if (i == 0) {
            for (int j = 0; j < 26; j++) {
                freq[j] = temp[j];
            }
        } else {
            for (int j = 0; j < 26; j++) {
                freq[j] = (freq[j] < temp[j]) ? freq[j] : temp[j];
            }
        }
    }
    char ans[51] = "";
    for (int i = 0; i < 26; i++) {
        for (int j = 0; j < freq[i]; j++) {
            char c = i + 'a';
            strcat(ans, &c);
        }
    }
    printf("%s\n", ans);
    return 0;
}
