#include <stdio.h>

#define MOD 1000000007

int main() {
    long long n, m;
    scanf("%lld %lld", &n, &m);
    long long x[n], y[m];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &x[i]);
    }
    for (long long i = 0; i < m; i++) {
        scanf("%lld", &y[i]);
    }
    long long total_area = 0;
    for (long long i = 0; i < n - 1; i++) {
        for (long long j = i + 1; j < n; j++) {
            for (long long k = 0; k < m - 1; k++) {
                for (long long l = k + 1; l < m; l++) {
                    total_area = (total_area + (x[j] - x[i]) * (y[l] - y[k])) % MOD;
                }
            }
        }
    }
    if (total_area < 0) {
        total_area += MOD;
    }
    printf("%lld\n", total_area);
    return 0;
}

