#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  vector<long long> x(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i];
  }

  vector<long long> y(m);
  for (int i = 0; i < m; ++i) {
    cin >> y[i];
  }

  long long total_area = 0;
  long long mod = 1e9 + 7;

  for (long long i = 0; i < n - 1; ++i) {
    for (long long j = i + 1; j < n; ++j) {
      for (long long k = 0; k < m - 1; ++k) {
        for (long long l = k + 1; l < m; ++l) {
          total_area = (total_area + (x[j] - x[i]) * (y[l] - y[k])) % mod;
        }
      }
    }
  }

  cout << (total_area + mod) % mod << endl;
  return 0;
}
