import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long m = sc.nextLong();
        long[] x = new long[(int) n];
        long[] y = new long[(int) m];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
        }
        for (int i = 0; i < m; i++) {
            y[i] = sc.nextLong();
        }
        long mod = 1000000007;
        long ans = 0;
        for (int i = 0; i < n - 1; i++) {
            for (int j = i + 1; j < n; j++) {
                for (int k = 0; k < m - 1; k++) {
                    for (int l = k + 1; l < m; l++) {
                        ans = (ans + (x[j] - x[i]) * (y[l] - y[k])) % mod;
                    }
                }
            }
        }
        System.out.println(ans);
    }
}
