#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
    a[i]--; 
  }

  vector<long long> b(n);
  for (int i = 0; i < n; ++i) {
    cin >> b[i];
    b[i]--; 
  }

  vector<long long> c(n);
  for (long long i = 0; i < n; ++i) {
    c[i] = i;
  }

  long long count = 0;
  long long mod = 1e9 + 7;

  do {
    vector<bool> eaten(n, false);
    bool fight = false;
    vector<long long> remaining;
    for (long long i = 0; i < n; ++i) remaining.push_back(i);

    while (!remaining.empty()) {
      long long best_a = -1, best_b = -1, best_c = -1;
      long long min_a = -1, min_b = -1, min_c = -1;

      for (long long x : remaining) {
        long long cur_a = -1;
        for (long long i = 0; i < n; i++) {
          if (a[i] == x) {
            cur_a = i;
            break;
          }
        }
        if (best_a == -1 || cur_a < min_a) {
          min_a = cur_a;
          best_a = x;
        }
      }
      for (long long x : remaining) {
        long long cur_b = -1;
        for (long long i = 0; i < n; i++) {
          if (b[i] == x) {
            cur_b = i;
            break;
          }
        }
        if (best_b == -1 || cur_b < min_b) {
          min_b = cur_b;
          best_b = x;
        }
      }
      for (long long x : remaining) {
        long long cur_c = -1;
        for (long long i = 0; i < n; i++) {
          if (c[i] == x) {
            cur_c = i;
            break;
          }
        }
        if (best_c == -1 || cur_c < min_c) {
          min_c = cur_c;
          best_c = x;
        }
      }

      if (best_a == best_b || best_a == best_c || best_b == best_c) {
        fight = true;
        break;
      }

      eaten[best_a] = true;
      eaten[best_b] = true;
      eaten[best_c] = true;
      vector<long long> next_remaining;
      for (long long x : remaining) {
        if (x != best_a && x != best_b && x != best_c) next_remaining.push_back(x);
      }
      remaining = next_remaining;
    }
    if (!fight) count = (count + 1) % mod;
  } while (next_permutation(c.begin(), c.end()));

  cout << count << endl;
  return 0;
}
