#include <stdio.h>
#include <string.h>

int main() {
    char S[100005], T[100005];
    int q, a, b, c, d;
    scanf("%s %s %d", S, T, &q);
    while (q--) {
        scanf("%d %d %d %d", &a, &b, &c, &d);
        int lenS = b - a + 1;
        int lenT = d - c + 1;
        char subS[lenS + 1], subT[lenT + 1];
        strncpy(subS, S + a - 1, lenS);
        subS[lenS] = '\0';
        strncpy(subT, T + c - 1, lenT);
        subT[lenT] = '\0';
        int countA_S = 0, countB_S = 0;
        int countA_T = 0, countB_T = 0;
        for (int i = 0; i < lenS; i++) {
            if (subS[i] == 'A')
                countA_S++;
            else
                countB_S++;
        }
        for (int i = 0; i < lenT; i++) {
            if (subT[i] == 'A')
                countA_T++;
            else
                countB_T++;
        }
        if (countA_S % 2 == countA_T % 2 && countB_S % 2 == countB_T % 2 && lenS >= lenT)
            printf("YES\n");
        else
            printf("NO\n");
    }
    return 0;
}

