#include <iostream>
#include <string>
#include <vector>

using namespace std;

string solve() {
  string s, t;
  cin >> s >> t;
  int q;
  cin >> q;

  for (int i = 0; i < q; ++i) {
    int a, b, c, d;
    cin >> a >> b >> c >> d;
    string s_sub = s.substr(a - 1, b - a + 1);
    string t_sub = t.substr(c - 1, d - c + 1);

    string cur = s_sub;
    bool possible = false;
    vector<string> q;
    q.push_back(cur);
    vector<bool> visited(1000000, false);

    int head = 0;
    while(head < q.size()){
        string curr = q[head++];
        if(curr == t_sub){
            possible = true;
            break;
        }
        if(visited[curr.length()*100000+stoi(curr,nullptr,2)]) continue;
        visited[curr.length()*100000+stoi(curr,nullptr,2)] = true;


        for (int j = 0; j < curr.length(); ++j) {
          string next = curr;
          if (next[j] == 'A') {
            next.replace(j, 1, "BB");
          } else {
            next.replace(j, 1, "AA");
          }
          bool added = false;
          for(int k=0; k<q.size(); ++k){
              if(q[k] == next) {added = true; break;}
          }
          if(!added) q.push_back(next);
        }

        for (int j = 0; j < curr.length() - 2; ++j) {
          if (curr.substr(j, 3) == "AAA" || curr.substr(j, 3) == "BBB") {
            string next = curr.substr(0, j) + curr.substr(j + 3);
            bool added = false;
            for(int k=0; k<q.size(); ++k){
                if(q[k] == next) {added = true; break;}
            }
            if(!added) q.push_back(next);
          }
        }
    }
    cout << (possible ? "YES" : "NO") << endl;
  }
  return "";
}

int main() {
  cout << solve() << endl;
  return 0;
}
