def solve():
    S = input()
    T = input()
    q = int(input())

    def normalize(s):
        s = s.replace('AAA', '')
        s = s.replace('BBB', '')
        return s

    def can_transform(s, t):
        s_norm = normalize(s)
        t_norm = normalize(t)

        if len(s_norm) != len(t_norm):
            return False


        for i in range(len(s_norm)):
          if (s_norm[i] == 'A' and t_norm[i] == 'B') or (s_norm[i] == 'B' and t_norm[i] == 'A'):
            return False
        return True

    for _ in range(q):
        a, b, c, d = map(int, input().split())
        s_sub = S[a - 1:b]
        t_sub = T[c - 1:d]

        if can_transform(s_sub, t_sub):
            print("YES")
        else:
            print("NO")

