#include <stdio.h>

#define MOD 1000000007

int main() {
    long long n;
    scanf("%lld", &n);
    long long dp[n + 1];
    dp[1] = 1;
    for (long long i = 2; i <= n; i++) {
        dp[i] = 0;
        for (long long j = 1; j <= i; j++) {
            dp[i] = (dp[i] + dp[j]) % MOD;
        }
    }
    long long ans = 0;
    for (long long i = 1; i <= n; i++) {
        ans = (ans + dp[i]) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
