#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n;
  cin >> n;

  long long mod = 1e9 + 7;

  if (n == 1) {
    cout << 1 << endl;
    return 0;
  }

  if (n == 2) {
    cout << 4 << endl;
    return 0;
  }

  vector<long long> dp(n + 1, 0);
  dp[1] = 1;
  dp[2] = 4;

  for (long long i = 3; i <= n; ++i) {
    dp[i] = (dp[i - 1] * 2 + dp[i-2]) % mod;

    long long sum = 0;
    for (long long j = 1; j <= i; j++){
        sum = (sum + dp[j]) % mod;
    }

    dp[i] = sum;

  }

  cout << dp[n] << endl;
  return 0;
}
