#include <stdio.h>
#include <stdlib.h>

int cmpfunc(const void * a, const void * b) {
   return ( *(long long*)a - *(long long*)b );
}

int main() {
    long long n;
    scanf("%lld", &n);
    long long arr[3 * n];
    for (long long i = 0; i < 3 * n; i++) {
        scanf("%lld", &arr[i]);
    }
    qsort(arr, 3 * n, sizeof(long long), cmpfunc);
    long long sum = 0;
    for (long long i = n; i < 2 * n; i++) {
        sum += arr[i];
    }
    printf("%lld\n", sum);
    return 0;
}
