import java.util.*;

public class Sushi {

    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt() - 1;
        }
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextInt() - 1;
        }
        sc.close();

        long ans = 0;
        List<Integer> p = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            p.add(i);
        }

        do {
            if (check(n, a, b, p)) {
                ans++;
            }
        } while (nextPermutation(p));

        System.out.println(ans % MOD);
    }

    static boolean check(int n, int[] a, int[] b, List<Integer> c) {
        boolean[] eaten = new boolean[n];
        int[] cArr = new int[n];
        for(int i=0; i<n; ++i) cArr[i] = c.get(i);

        for (int i = 0; i < n; i++) {
            int bestA = -1;
            int bestB = -1;
            int bestC = -1;

            int minA = Integer.MAX_VALUE;
            int minB = Integer.MAX_VALUE;
            int minC = Integer.MAX_VALUE;

            for (int j = 0; j < n; j++) {
                if (!eaten[a[j]] && j < minA) {
                    minA = j;
                    bestA = a[j];
                }
                if (!eaten[b[j]] && j < minB) {
                    minB = j;
                    bestB = b[j];
                }
                if (!eaten[cArr[j]] && j < minC) {
                    minC = j;
                    bestC = cArr[j];
                }
            }

            if (bestA == bestB || bestA == bestC || bestB == bestC) return false;

            eaten[bestA] = true;
            eaten[bestB] = true;
            eaten[bestC] = true;

        }
        return true;
    }

    static boolean nextPermutation(List<Integer> p) {
        int n = p.size();
        int i = n - 2;
        while (i >= 0 && p.get(i) >= p.get(i + 1)) {
            i--;
        }
        if (i < 0) return false;
        int j = n - 1;
        while (p.get(i) >= p.get(j)) {
            j--;
        }
        Collections.swap(p, i, j);
        Collections.reverse(p.subList(i + 1, n));
        return true;
    }
}
