import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            graph.get(u).add(v);
            graph.get(v).add(u);
        }
        int q = sc.nextInt();
        int[] colors = new int[n];
        for (int i = 0; i < q; i++) {
            int v = sc.nextInt() - 1;
            int d = sc.nextInt();
            int c = sc.nextInt();
            Queue<Integer> queue = new LinkedList<>();
            queue.offer(v);
            int[] dist = new int[n];
            Arrays.fill(dist, -1);
            dist[v] = 0;
            while (!queue.isEmpty()) {
                int u = queue.poll();
                if (dist[u] <= d) {
                    colors[u] = c;
                    for (int neighbor : graph.get(u)) {
                        if (dist[neighbor] == -1) {
                            dist[neighbor] = dist[u] + 1;
                            queue.offer(neighbor);
                        }
                    }
                }
            }
        }
        for (int i = 0; i < n; i++) {
            System.out.println(colors[i]);
        }
        sc.close();
    }
}
