#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

typedef struct {
    int c;
    long long w;
} Ball;

int cmp(const void *a, const void *b) {
    return ((Ball *)a)->c - ((Ball *)b)->c;
}

int main() {
    int N, i, j, k;
    long long X, Y;
    scanf("%d %lld %lld", &N, &X, &Y);
    Ball balls[N];
    for (i = 0; i < N; i++) {
        scanf("%d %lld", &balls[i].c, &balls[i].w);
    }
    qsort(balls, N, sizeof(Ball), cmp);
    long long count = 1;
    int colors[N];
    for (i = 0; i < N; i++) {
        colors[i] = balls[i].c;
    }
    for (i = 1; i < N; i++) {
        if (colors[i] != colors[i - 1]) {
            count = (count * i) % MOD;
        }
    }
    printf("%lld\n", count);
    return 0;
}
