#include <iostream>
#include <vector>
#include <map>
#include <set>

using namespace std;

int main() {
  long long n, x, y;
  cin >> n >> x >> y;

  vector<pair<long long, long long>> balls(n);
  for (int i = 0; i < n; ++i) {
    cin >> balls[i].first >> balls[i].second;
  }

  map<long long, vector<long long>> color_map;
  for (int i = 0; i < n; ++i) {
    color_map[balls[i].first].push_back(i);
  }

  set<vector<long long>> distinct_sequences;
  vector<long long> current_sequence(n);
  for (int i = 0; i < n; ++i) {
    current_sequence[i] = balls[i].first;
  }
  distinct_sequences.insert(current_sequence);


  long long count = 0;
  function<void(vector<pair<long long,long long>>)> permute = [&](vector<pair<long long,long long>> arr) {
    vector<long long> seq(n);
    for(int i=0; i<n; ++i) seq[i] = arr[i].first;
    if(distinct_sequences.find(seq) == distinct_sequences.end()){
        distinct_sequences.insert(seq);
        count++;
    }
  };


  function<void(vector<pair<long long,long long>>, int)> solve = [&](vector<pair<long long,long long>> arr, int k) {
    if(k == n){
        permute(arr);
        return;
    }

    for(int i=k; i<n; ++i){
        for(int j=k; j<n; ++j){
            if(arr[i].first == arr[j].first && arr[i].second + arr[j].second <=x){
                swap(arr[i],arr[j]);
                solve(arr,k+1);
                swap(arr[i],arr[j]);
            } else if (arr[i].first != arr[j].first && arr[i].second + arr[j].second <= y){
                swap(arr[i],arr[j]);
                solve(arr,k+1);
                swap(arr[i],arr[j]);
            }
        }
    }
  };

  solve(balls,0);
  cout << distinct_sequences.size() << endl;

  return 0;
}
