MOD = 10**9 + 7

def solve():
    n, x, y = map(int, input().split())
    balls = []
    for _ in range(n):
        balls.append(list(map(int, input().split())))

    colors = {}
    for i in range(n):
        if balls[i][0] not in colors:
            colors[balls[i][0]] = []
        colors[balls[i][0]].append(i)

    adj = [[] for _ in range(n)]
    for i in range(n):
        for j in range(i + 1, n):
            if balls[i][0] == balls[j][0]:
                if balls[i][1] + balls[j][1] <= x:
                    adj[i].append(j)
                    adj[j].append(i)
            else:
                if balls[i][1] + balls[j][1] <= y:
                    adj[i].append(j)
                    adj[j].append(i)

    visited = [False] * n
    groups = []
    for i in range(n):
        if not visited[i]:
            group = []
            q = [i]
            visited[i] = True
            while q:
                u = q.pop(0)
                group.append(u)
                for v in adj[u]:
                    if not visited[v]:
                        visited[v] = True
                        q.append(v)
            groups.append(group)

    ans = 1
    for group in groups:
        group_colors = set()
        for i in group:
            group_colors.add(balls[i][0])
        ans = (ans * len(group_colors)) % MOD

    print(ans)

solve()
