#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, V;
    scanf("%d %d", &N, &V);
    long long x[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &x[i]);
    }
    for (int i = 0; i < N; i++) {
        int possible = 1;
        long long current_water = V;
        long long current_hump = V;
        int visited[N];
        for (int j = 0; j < N; j++) {
            visited[j] = 0;
        }
        visited[i] = 1;
        int count = 1;
        int current_oasis = i;
        while (count < N) {
            int next_oasis = -1;
            long long min_dist = -1;
            for (int j = 0; j < N; j++) {
                if (!visited[j]) {
                    long long dist = abs(x[j] - x[current_oasis]);
                    if (current_water >= dist) {
                        if (next_oasis == -1 || dist < min_dist) {
                            next_oasis = j;
                            min_dist = dist;
                        }
                    }
                }
            }
            if (next_oasis == -1) {
                if (current_hump <= 0) {
                    possible = 0;
                    break;
                }
                current_hump /= 2;
                current_water = 0;
                for (int j = 0; j < N; j++) {
                    if (!visited[j]) {
                        next_oasis = j;
                        break;
                    }
                }

            }
            if (next_oasis != -1) {
                current_water -= abs(x[next_oasis] - x[current_oasis]);
                current_water += current_hump;
                visited[next_oasis] = 1;
                current_oasis = next_oasis;
                count++;
            }

        }
        if (possible) {
            printf("Possible\n");
        } else {
            printf("Impossible\n");
        }
    }
    return 0;
}
