#include <iostream>
#include <vector>

using namespace std;

int main() {
    long long n, v;
    cin >> n >> v;

    vector<long long> x(n);
    for (int i = 0; i < n; ++i) {
        cin >> x[i];
    }

    for (int i = 0; i < n; ++i) {
        bool possible = true;
        vector<bool> visited(n, false);
        long long current_pos = x[i];
        long long current_water = v;
        visited[i] = true;
        int visited_count = 1;

        while (visited_count < n) {
            long long next_pos = -1;
            long long min_dist = -1;

            for (int j = 0; j < n; ++j) {
                if (!visited[j]) {
                    long long dist = abs(x[j] - current_pos);
                    if (current_water >= dist) {
                        if (next_pos == -1 || dist < min_dist) {
                            next_pos = x[j];
                            min_dist = dist;
                        }
                    }
                }
            }

            if (next_pos == -1) {
                if (current_water > 0) {
                    current_water /= 2;
                    continue;
                } else {
                    possible = false;
                    break;
                }
            }

            current_water -= min_dist;
            current_pos = next_pos;
            current_water = v;
            visited[find(x.begin(),x.end(),current_pos)-x.begin()] = true;
            visited_count++;
        }
        cout << (possible ? "Possible" : "Impossible") << endl;
    }
    return 0;
}
