def solve():
    n, v = map(int, input().split())
    x = list(map(int, input().split()))

    for start_node in range(n):
        possible = True
        visited = [False] * n
        current_node = start_node
        current_water = v
        visited[current_node] = True

        while True:
            next_unvisited = -1
            min_dist = float('inf')
            for i in range(n):
                if not visited[i]:
                    dist = abs(x[i] - x[current_node])
                    if dist < min_dist:
                        min_dist = dist
                        next_unvisited = i

            if next_unvisited == -1:
                break

            if current_water >= min_dist:
                current_water -= min_dist
                current_node = next_unvisited
                visited[current_node] = True
            else:

                jumped = False
                for i in range(n):
                    if not visited[i]:
                        if current_water > 0:
                            current_water //= 2
                            current_node = i
                            visited[i] = True
                            jumped = True
                            break
                if not jumped:
                    possible = False
                    break

        if all(visited):
            print("Possible")
        else:
            print("Impossible")

solve()
