#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

int compare(const void *a, const void *b) {
    return *(int *)a - *(int *)b;
}

int main() {
    int N;
    scanf("%d", &N);
    int a[2 * N - 1];
    for (int i = 0; i < 2 * N - 1; i++) {
        scanf("%d", &a[i]);
    }
    qsort(a, 2 * N - 1, sizeof(int), compare);
    long long count = 0;
    do {
        int b[N];
        for (int i = 0; i < N; i++) {
            int arr[2 * i + 1];
            for (int j = 0; j <= 2 * i; j++) {
                arr[j] = a[j];
            }
            qsort(arr, 2 * i + 1, sizeof(int), compare);
            b[i] = arr[i];
        }
        int flag = 1;
        for (int i = 0; i < count; i++) {
            int flag2 = 1;
            for (int j = 0; j < N; j++) {
                if (b[j] != a[j]) {
                    flag2 = 0;
                    break;
                }
            }
            if (flag2) {
                flag = 0;
                break;
            }
        }
        if (flag) {
            count++;
        }
    } while (next_permutation(a, a + 2 * N - 1));
    printf("%lld\n", count % MOD);
    return 0;
}
