#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(2 * n - 1);
  for (int i = 0; i < 2 * n - 1; ++i) {
    cin >> a[i];
  }

  vector<long long> p(2 * n - 1);
  for (int i = 0; i < 2 * n - 1; ++i) {
    p[i] = i;
  }

  vector<vector<long long>> distinct_b;
  long long count = 0;

  do {
    vector<long long> b(n);
    vector<long long> current_a;
    for (int i = 0; i < n; ++i) {
      current_a.push_back(a[p[i]]);
      sort(current_a.begin(), current_a.end());
      b[i] = current_a[(current_a.size() -1) / 2];
    }
    bool found = false;
    for (const auto& v : distinct_b) {
      if (v == b) {
        found = true;
        break;
      }
    }
    if (!found) {
      distinct_b.push_back(b);
      count++;
    }
  } while (next_permutation(p.begin(), p.end()));

  cout << count << endl;
  return 0;
}
