#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    long long a[N], b[N], c[M], d[M];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &a[i], &b[i]);
    }
    for (int i = 0; i < M; i++) {
        scanf("%lld %lld", &c[i], &d[i]);
    }
    for (int i = 0; i < N; i++) {
        long long min_dist = -1;
        int min_index = -1;
        for (int j = 0; j < M; j++) {
            long long dist = abs(a[i] - c[j]) + abs(b[i] - d[j]);
            if (min_index == -1 || dist < min_dist) {
                min_dist = dist;
                min_index = j + 1;
            }
        }
        printf("%d\n", min_index);
    }
    return 0;
}

