#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  vector<pair<long long, long long>> students(n);
  for (int i = 0; i < n; ++i) {
    cin >> students[i].first >> students[i].second;
  }

  vector<pair<long long, long long>> checkpoints(m);
  for (int i = 0; i < m; ++i) {
    cin >> checkpoints[i].first >> checkpoints[i].second;
  }

  for (int i = 0; i < n; ++i) {
    long long min_dist = -1;
    long long min_index = -1;
    for (int j = 0; j < m; ++j) {
      long long dist = abs(students[i].first - checkpoints[j].first) + abs(students[i].second - checkpoints[j].second);
      if (min_index == -1 || dist < min_dist) {
        min_dist = dist;
        min_index = j + 1;
      }
    }
    cout << min_index << endl;
  }
  return 0;
}
