import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        long[][] students = new long[n][2];
        long[][] checkpoints = new long[m][2];
        for (int i = 0; i < n; i++) {
            students[i][0] = sc.nextLong();
            students[i][1] = sc.nextLong();
        }
        for (int i = 0; i < m; i++) {
            checkpoints[i][0] = sc.nextLong();
            checkpoints[i][1] = sc.nextLong();
        }
        for (int i = 0; i < n; i++) {
            long minDist = Long.MAX_VALUE;
            int minIndex = -1;
            for (int j = 0; j < m; j++) {
                long dist = Math.abs(students[i][0] - checkpoints[j][0]) + Math.abs(students[i][1] - checkpoints[j][1]);
                if (dist < minDist) {
                    minDist = dist;
                    minIndex = j + 1;
                }
            }
            System.out.println(minIndex);
        }
        sc.close();
    }
}
