#include <stdio.h>
#include <math.h>

int countDigits(long long n) {
    if (n == 0) return 1;
    if (n < 0) n = -n;
    return floor(log10(n)) + 1;
}

int main() {
    long long n;
    scanf("%lld", &n);
    long long min_f = 100; 
    for (long long a = 1; a * a <= n; ++a) {
        if (n % a == 0) {
            long long b = n / a;
            min_f = fmin(min_f, fmax(countDigits(a), countDigits(b)));
        }
    }
    printf("%lld\n", min_f);
    return 0;
}
