#include <iostream>
#include <algorithm>
#include <cmath>

using namespace std;

int digits(long long n) {
  if (n == 0) return 1;
  return floor(log10(n)) + 1;
}

int main() {
  long long n;
  cin >> n;

  long long min_f = 1e9;
  for (long long a = 1; a * a <= n; ++a) {
    if (n % a == 0) {
      long long b = n / a;
      min_f = min(min_f, max(digits(a), digits(b)));
    }
  }
  cout << min_f << endl;
  return 0;
}
