#include <stdio.h>
#include <stdlib.h>

int main() {
    long long n, a, b;
    scanf("%lld %lld %lld", &n, &a, &b);
    long long v[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &v[i]);
    }
    long long count = 0;
    double max_mean = 0;
    long long max_count = 0;
    for (long long i = 0; i < (1LL << n); i++) {
        long long num_selected = 0;
        long long sum = 0;
        for (long long j = 0; j < n; j++) {
            if ((i >> j) & 1) {
                num_selected++;
                sum += v[j];
            }
        }
        if (num_selected >= a && num_selected <= b) {
            double mean = (double)sum / num_selected;
            if (mean > max_mean) {
                max_mean = mean;
                max_count = 1;
            } else if (mean == max_mean) {
                max_count++;
            }
        }
    }
    printf("%.6lf\n", max_mean);
    printf("%lld\n", max_count);
    return 0;
}
