#include <iostream>
#include <vector>
#include <numeric>
#include <iomanip>
#include <algorithm>

using namespace std;

int main() {
    long long n, a, b;
    cin >> n >> a >> b;

    vector<long long> v(n);
    for (int i = 0; i < n; ++i) {
        cin >> v[i];
    }

    sort(v.begin(), v.end(), greater<long long>());

    double max_mean = 0;
    long long count = 0;

    for (long long i = a; i <= b; ++i) {
        for (long long j = 0; j < (1LL << n); ++j) {
            if (__builtin_popcountll(j) == i) {
                vector<long long> subset;
                for (long long k = 0; k < n; ++k) {
                    if ((j >> k) & 1) {
                        subset.push_back(v[k]);
                    }
                }
                double current_mean = (double)accumulate(subset.begin(), subset.end(), 0LL) / i;
                if (current_mean > max_mean) {
                    max_mean = current_mean;
                    count = 1;
                } else if (current_mean == max_mean) {
                    count++;
                }
            }
        }
    }

    cout << fixed << setprecision(10) << max_mean << endl;
    cout << count << endl;

    return 0;
}
