import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int a = sc.nextInt();
        int b = sc.nextInt();
        long[] v = new long[n];
        for (int i = 0; i < n; i++) {
            v[i] = sc.nextLong();
        }
        Arrays.sort(v);
        double maxAvg = 0;
        long count = 0;
        for (int i = 0; i < (1 << n); i++) {
            int numItems = 0;
            long sum = 0;
            List<Long> selectedItems = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    numItems++;
                    sum += v[n - 1 - j];
                    selectedItems.add(v[n - 1 -j]);
                }
            }
            if (numItems >= a && numItems <= b) {
                double avg = (double) sum / numItems;
                if (avg > maxAvg) {
                    maxAvg = avg;
                    count = 1;
                } else if (avg == maxAvg) {
                    count++;
                }
            }
        }
        System.out.printf("%.6f\n", maxAvg);
        System.out.println(count);
    }
}
